dataset.update:
UPDATE boat_part
SET boat_id = {{boat_id}},
    name = {{name}}
    change_user = {{__username}},
    change_date = datetime ('now')
WHERE id = {{id}};

dataset.insert:
INSERT INTO boat_part (boat_id, name, change_user, change_date)
VALUES ({{boat_id}}, {{name}},
        {{__username}}, datetime ('now'));
